
public class Swap2Variable { // Swapping 2 variables by "Bro Code" from youtube

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String x = "water";
		String y = "Kool-Aid";
		String temp;
		
		temp = x; // water
		x = y; // x = Kool-Aid
		y = temp; // y = water
		
		System.out.println("x: " + x);
		System.out.println("y: " + y);
	}

}
